import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-asign-medical-staff',
  templateUrl: './asign-medical-staff.component.html',
  //styleUrls: ['./asign-medical-staff.component.css']
})
export class AssignMedicalStaffComponent {
  medicoId: number = 0;
  pacienteId: number = 0;

  constructor(private http: HttpClient) {}

  asignarMedico(event: Event) {
    event.preventDefault();

    if (!this.medicoId || !this.pacienteId) {
      console.log('Debe completar ambos campos');
      return;
    }

    const url = 'personal_in_charge/api/assign-medical-staff';
    const data = {
      medical_staff_id: this.medicoId,
      patient_id: this.pacienteId
    };

    this.http.post(url, data).subscribe(
      response => {
        console.log('Asignación exitosa:', response);
        // Realizar acciones adicionales si es necesario
      },
      error => {
        console.log('Error en la asignación:', error);
        // Manejar errores si es necesario
      }
    );
  }
}
