import { Component } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';

@Component({
  selector: 'app-assign-medical-staff',
  templateUrl: './asign-medical-staff.component.html',
  //styleUrls: ['./assign-medical-staff.component.css']
})
export class AssignMedicalStaffComponent {
  constructor(private http: HttpClient) { }

  asignarMedico(event: Event) {
    event.preventDefault();

    const form = event.target as HTMLFormElement;

const medicoId = form['medicoId'].value;
const pacienteId = form['pacienteId'].value;

if (!medicoId || isNaN(medicoId)) {
  console.error('El ID del médico es inválido');
  return;
}

if (!pacienteId || isNaN(pacienteId)) {
  console.error('El ID del paciente es inválido');
  return;
}

const body = {
  medical_staff_id: parseInt(medicoId),
  patient_id: parseInt(pacienteId)
};

const httpOptions = {
  headers: new HttpHeaders({
    'Content-Type': 'application/json'
  })
};

this.http.post('http://localhost:8000/personal_in_charge/api/assign-medical-staff', body, httpOptions)
  .subscribe(
    (response) => {
      console.log('Asignación de médico exitosa', response);
      form.reset();
    },
    (error) => {
      console.error('Error al asignar médico', error);
    }
  );
  }
}
