import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-assign-medical-staff',
  templateUrl: './assign-medical-staff.component.html',
  styleUrls: ['./assign-medical-staff.component.css']
})
export class AssignMedicalStaffComponent {
  constructor(private http: HttpClient) { }

  asignarMedico(event: Event) {
    event.preventDefault();

    const form = event.target as HTMLFormElement;

    const medicoId = form['medicoId'].value;
    const pacienteId = form['pacienteId'].value;

    const body = {
      medical_staff_id: medicoId,
      patient_id: pacienteId
    };

    this.http.post('http://localhost:8000/personal_in_charge/api/assign-medical-staff', body)
      .subscribe(
        (response) => {
          console.log('Asignación de médico exitosa', response);
          form.reset();
        },
        (error) => {
          console.error('Error al asignar médico', error);
        }
      );
  }
}
