import { Component } from '@angular/core';
import { HttpClient} from '@angular/common/http';

@Component({
  selector: 'app-list-users',
  templateUrl: './list-users.component.html',
  styleUrls: ['./list-users.component.css']
})
export class ListUsersComponent {
  dataList: any[] = [];

  constructor(private http: HttpClient){
    this.getDataFromBackend();
  }

  getDataFromBackend(){
    this.http.get<any[]>('http://localhost:8000/users/users').subscribe(data => {
      this.dataList = data;
    });
  }

  eliminarUsuario(id:number){
    this.http.delete<any[]>(`http://localhost:8000/users/users/${id}`).subscribe(()=> {
      this.dataList = this.dataList.filter((u)=>u.id !== id);
    })
  }
}
<div class="user-list">
  <h3>Usuarios Registrados</h3>
    <table>
      <thead>
        <tr>
            <th>Nombre</th>
            <th>Apellido</th>
            <th>Cedula</th>
            <th>Edad</th>
            <th>Telefono</th>
            <th>Correo</th>
            <th>Direccion</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let item of dataList">
            <td>{{ item.name }}</td>
            <td>{{ item.last_name }}</td>
            <td>{{ item.identification_card }}</td>
            <td>{{ item.age }}</td>
            <td>{{ item.phone }}</td>
            <td>{{ item.email }}</td>
            <td>{{ item.address }}</td>
        </tr>
      </tbody>
    </table>
  </div>
