import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-asign-medical-staff',
  templateUrl: './asign-medical-staff.component.html',
  styles: []
})
export class AsignMedicalStaffComponent {
  medicalStaffId: number;
  patientId: number;

  constructor(private http: HttpClient) {}

  asignarPersonalMedico() {
    const url = 'http://localhost:8000/personal_in_charge/api/assign-medical-staff';
    const payload = { medical_staff_id: this.medicalStaffId, patient_id: this.patientId };

    this.http.post(url, payload).subscribe(
      (response) => {
        console.log(response); // Maneja la respuesta del backend según tus necesidades
      },
      (error) => {
        console.error(error); // Maneja el error según tus necesidades
      }
    );
  }
}

<input type="text" [(ngModel)]="medicalStaffId" placeholder="ID de personal médico">
<input type="text" [(ngModel)]="patientId" placeholder="ID de paciente">
<button (click)="asignarPersonalMedico()">Asignar Personal Médico</button>
