import { Component } from '@angular/core';
import { ListUsersComponent } from './users/list-users/list-users.component';
import { CreatePatientComponent } from './patients/create-patient/create-patient.component';
import { CreateFamiliarComponent } from './familiars/create-familiar/create-familiar.component';
import { GetAllPatientsComponent} from './patients/get-all-patients/get-all-patients.component';
import { CreateMedicalStaffComponent } from './medical-staff/create-medical-staff/create-medical-staff.component';
import { GetAllMedicalStaffComponent } from './medical-staff/get-all-medical-staff/get-all-medical-staff.component';
import { AsignMedicalStaffComponent } from './medical-staff/asign-medical-staff/asign-medical-staff.component';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent  {
  componenteSeleccionado: any;
  sidebarVisible = false;

  toggleSidebar() {
    this.sidebarVisible = !this.sidebarVisible;
  }

  listarUsuarios() {
    this.componenteSeleccionado = ListUsersComponent;
  }

  registrarFamiliar(){
    this.componenteSeleccionado = CreateFamiliarComponent;
  }

  registrarPaciente(){
    this.componenteSeleccionado = CreatePatientComponent;
  }

  listarPacientes(){
    this.componenteSeleccionado = GetAllPatientsComponent;
  }

  listarPersonalMedico(){
    this.componenteSeleccionado = GetAllMedicalStaffComponent;
  }


  registrarPersonalMedico(){
    this.componenteSeleccionado = CreateMedicalStaffComponent;
  }

  asignarPersonalMedico(){
    this.componenteSeleccionado = AsignMedicalStaffComponent;
  }

}
<nav class="navbar navbar-expand-lg navbar-light bg-light">
  <a class="navbar-brand" href="#">
    <img src="../../images/logo.jpeg" alt="Logo de la empresa" width="30" height="30" class="navbar-logo">
  </a>
  <p class="navbar-title">HOSPITALIZACION EN CASA</p>
</nav>
<div class="container">
  <div class="sidebar">
    <!-- Contenido de la barra lateral -->
    <button (click)="listarUsuarios()">Mostrar todos los Usuarios</button>
    <button (click)="listarPacientes()">Mostrar todos los Pacientes</button>
    <button (click)="listarPersonalMedico()">Mostrar Personal Medico</button>
    <button (click)="registrarFamiliar()">Registrar Familiar</button>
    <button (click)="registrarPaciente()">Registrar Paciente</button>
    <button (click)="registrarPersonalMedico()">Registrar Personal Medico</button>
    <button (click)="asignarPersonalMedico()">Asignar Personal Medico</button>
  </div>
  <div class="main-content">
    <!-- Contenido de donde van los componentes -->
    <ng-container *ngIf="componenteSeleccionado">
      <ng-container *ngComponentOutlet="componenteSeleccionado"></ng-container>
    </ng-container>
  </div>
</div>
